import { createRef, useEffect } from "react"
import { useAppHooks } from "../../context/AppContext"

const Modal = () => {
  const modalRef = createRef()
  const modalEl = document.getElementById("modalTest")
  const {useModal: [modal, setModal]} = useAppHooks()

  const handleAction = () => {
    modal.action.method()
    setModal(null)
  }

  useEffect(() => {
    if (modal) {
      $("#modalTest").modal("toggle")
      // console.log($("#modalTest"))
      // console.log(modalRef)
      // document.getElementById("modalTest").modal("toggle")
      if (modalEl) {
        // console.log(modalEl)
        // modalEl.modal("toggle")
        // modalRef.current.getElementById("modalTest").modal("toggle")
      }
    }
  }, [modal])

  return (
    modal &&
    <div className="modal fade border border-danger"
      tabIndex={-1}
      id="modalTest"
      aria-labelledby="modalLabel"
      ref={modalRef}
    >
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="modalTestLabel">{modal.title}</h5>
          </div>
          <div className="modal-body">
            <p>{modal.body}</p>
          </div>
          <div className="modal-footer">
            <button onClick={() => setModal(null)} className="btn btn-secondary" data-dismiss="modal">{modal.dismiss || "Close"}</button>
            <button onClick={handleAction} className={`btn btn-${modal.action.role}`} data-dismiss="modal">{modal.action.text}</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Modal
