import { useContext } from "react"
import { AppContext, useAppHooks } from "../../context/AppContext"
import useCart from "../../hooks/useCart"

const CartNav = () => {
    // const cart = useCart()
    const [cart, setCart] = useAppHooks()

    // console.log(cart)

  return (
      <>
        <span>
            Cart
        </span>
        <span className="ml-2 px-2 py-1 bg-dark rounded-circle">
            {cart && cart.totalProductsCount ? cart.totalProductsCount : 0}
        </span>
        <span className="ml-2">
            {cart && cart.totalProductsPrice + " €"}
        </span>
      </>
  )
}

export default CartNav
