const { useAppHooks } = require("../../context/AppContext")

const Toast = () => {
    const {useToast: [toast, setToast]} = useAppHooks()
    console.log(toast)
  return (
      toast &&
      <div aria-live="polite" aria-atomic="true" className="d-flex justify-content-center align-items-center" style={{height: 200}}>
          <div className="toast" role={toast.role} data-autohide="false">
              <div className="toast-header">
                  <strong className="mr-auto">{toast.title}</strong>
                  <button className="ml-2 mb-1 close" datadismiss="toast" aria-label="Close">
                      <span>&times;</span>
                  </button>
              </div>
              <div className="toast-body">
                  {toast.body}
              </div>
          </div>
      </div>
  )
}

export default Toast
