import { useContext } from "react"
import { AppContext } from "../../context/AppContext"

const AddToCartBtn = ({ product, variationValue, quantity }) => {
    const [cart, setCart] = useContext(AppContext)

    const handleAddToCart = () => {
        if (cart) {}
        else {
            let newCart = cart
            console.log(product.variations)
            let productVarition = product.variations.nodes.find(variation => variation.attributes.nodes[0].value === variationValue)
            if (productVarition) {
                newCart.push({quantity, productVarition})
                setCart(newCart)
            }
        }
        console.log(cart)
    }

  return (
      <>
        <button
            disabled={!variationValue || quantity === 0}
            className={`btn btn-${(variationValue && quantity > 0) ? "success" : "dark"} w-100 block`}
            onClick={handleAddToCart}
        >
            ADD TO CART
        </button>
      </>
  )
}

export default AddToCartBtn
