export const createCart = (product, quantity, variationValue) => {
    let rawPrice = parseFloat(product.price.replace(",", ".").substring(1))
    let indexVariation = product.variations.nodes.findIndex(variation => variation.attributes.nodes[0].value === variationValue)
    if (indexVariation > -1) {
        const newProduct = {
            quantity,
            totalPrice: (quantity * rawPrice).toFixed(2),
            variation: variationValue,
            productVariation: product
        }
        const items = [newProduct]
        return {
            items,
            totalProductsCount: 1,
            totalProductsPrice: parseFloat(newProduct.totalPrice),
        }
    }
    return null
}

export const updateCart = (cart, product, quantity, variationValue) => {
    const isProductMatchIndex = cart.items.findIndex(item => item.variation === variationValue)
    let rawPrice = parseFloat(product.price.replace(",", ".").substring(1))
    if (isProductMatchIndex > -1) {
        cart.items[isProductMatchIndex].quantity += quantity
        cart.items[isProductMatchIndex].totalPrice = (rawPrice * cart[isProductMatchIndex].quantity).toFixed(2)
        cart.totalProductsPrice = cart.items.reduce((acc, item) => acc + parseFloat(item.totalPrice), 0)
        return cart
    }
    else {
        let items = [
            ...cart.items,
            {
                quantity,
                totalPrice: (quantity * rawPrice).toFixed(2),
                productVariation: product,
                variation: variationValue
            }
        ]

        return {
            totalProductsPrice: items.reduce((acc, item) => acc + parseFloat(item.totalPrice), 0),
            totalProductsCount: cart.totalProductsCount + 1,
            items
        }
    }
}
