import { compare2Obj } from "../utils/obj.util"

const { useContext, useEffect, useState } = require("react")
const { AppContext } = require("../context/AppContext")
const { getCart } = require("../utils/cart.util")

const useCart = () => {
    const [cart, setCart] = useState(null)

    useEffect(() => {
        if (process.browser) {
            if (getCart()) {
                const comparedCart = getCart()
                if (!cart || !compare2Obj(cart, comparedCart)) {
                    setCart(comparedCart)
                }
            }
        }
    }, [getCart, cart])

    return cart
}

export default useCart
