import { useContext } from "react"
import { AppContext } from "../../context/AppContext"

const AddToCartBtn = ({ product, variationValue, quantity }) => {
    const [cart, setCart] = useContext(AppContext)

    const handleAddToCart = () => {
        if (cart) {}
        else {
            let rawPrice = product.price.shift()
            console.log(quantity)
            console.log(rawPrice)
            console.log(quantity * product.price)
            let newCart = cart || []
            let indexVariation = product.variations.nodes.findIndex(variation => variation.attributes.nodes[0].value === variationValue)
            if (indexVariation) {
                let newProduct = {
                    ...product,
                    variations: {
                        nodes: [product.variations.nodes[indexVariation]]
                    }}
                newCart.push({
                    totalPrice: (quantity * (+product.price)).toFixed(2),
                    quantity,
                    productVariation: newProduct
                })
                setCart(newCart)
            }
        }
    }

    console.log(cart)

  return (
      <>
        <button
            disabled={!variationValue || quantity === 0}
            className={`btn btn-${(variationValue && quantity > 0) ? "success" : "dark"} w-100 block`}
            onClick={handleAddToCart}
        >
            ADD TO CART
        </button>
      </>
  )
}

export default AddToCartBtn
