import { useEffect, useRef } from "react"

const { useAppHooks } = require("../../context/AppContext")

const Toast = () => {
    const toastRef = useRef()
    const {useToast: [toast, setToast]} = useAppHooks()
    console.log(toastRef)

    useEffect(() => {
        if (toast && toastRef && toastRef.current) {
            // toastRef.current.show()
        }
    }, [toast, toastRef])

    return (
        toast &&
        <div
            className="border border-danger position-fixed fixed-bottom d-flex justify-content-center align-items-end"
            aria-live="polite"
            aria-atomic="true"
        >
            <div
                className="toast"
                role={toast.role}
                aria-live="assertive"
                aria-atomic="true"
                ref={toastRef}
            >
                <div className="toast-header">
                    <strong className="mr-auto">{toast.title}</strong>
                    <button type="button" className="ml-2 mb-1 close" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="toast-body">
                    {toast.body}
                </div>
            </div>
        </div>
    )
}

export default Toast
