import { productRawPrice } from "./product.util"

export const productInCart = (cart, product) => {
    const productFound = cart.items.map()
}

export const getCart = () => {
    if (localStorage) {
        return JSON.parse(localStorage.getItem("woo-next-cart"))
    }
    return null
}

export const createCart = (product, quantity, variationValue) => {
    let rawPrice = productRawPrice(product.price)
    let indexVariation = product.variations.nodes.findIndex(variation => variation.attributes.nodes[0].value === variationValue)
    if (indexVariation > -1) {
        const newProduct = {
            quantity,
            totalPrice: parseFloat((quantity * rawPrice).toFixed(2)),
            variation: variationValue,
            productVariation: product
        }
        const items = [newProduct]
        const newCart = {
            items,
            totalProductsCount: 1,
            totalProductsPrice: newProduct.totalPrice,
        }
        localStorage.setItem("woo-next-cart", JSON.stringify(newCart))
        return newCart
    }
    return null
}

export const updateCartByIndex = (cart, index, quantity, variationValue) => {
    cart.items[index].quantity += quantity
    cart.items[index].variation = variationValue
    cart.items[index].totalPrice = parseFloat((rawPrice * cart.items[isProductMatchIndex].quantity).toFixed(2))
    cart.totalProductsPrice = cart.items.reduce((acc, item) => acc + parseFloat(item.totalPrice), 0)
    localStorage.setItem("woo-next-cart", JSON.stringify(cart))
    return cart
}

export const updateCartByProduct = (cart, product,quantity, variationValue) => {
    const rawPrice = productRawPrice(product.price)
    const index = cart.items.find(item => item.productVariation.name === product.name && item.variation === variationValue)
    if (index > -1) {
        return updateCartByIndex(cart, index, quantity, variationValue)
    }
    else {
        let items = [
            ...cart.items,
            {
                quantity,
                totalPrice: (quantity * rawPrice).toFixed(2),
                productVariation: product,
                variation: variationValue
            }                           
        ]

        const updatedCart = {
            totalProductsPrice: items.reduce((acc, item) => acc + parseFloat(item.totalPrice), 0),
            totalProductsCount: cart.totalProductsCount + 1,
            items
        }
        localStorage.setItem("woo-next-cart", JSON.stringify(updatedCart))
        return updatedCart
    }
}

export const removeItemFromCart = (cart, indexProduct) => {
    if (cart.totalProductsCount - 1 === indexProduct) {
        localStorage.removeItem("woo-next-cart")
        return null
    }
    const updatedCart = {
        items: cart.items.filter((item, index) => index !== indexProduct),
        totalProductsCount: cart.totalProductsCount - 1,
        totalProductsPrice: cart.totalProductsPrice - cart.items[indexProduct].totalPrice
    }
    localStorage.setItem("woo-next-cart", JSON.stringify(updatedCart))
    return updatedCart
}

export const removeCart = () => {
    localStorage.removeItem("woo-next-cart")
    return null
}
