import { useAppHooks } from "../../context/AppContext"
import { createCart, productInCart, updateCartByIndex, updateCartByProduct } from "../../utils/cart.util"

const AddToCartBtn = ({ product, variationValue, quantity, setQuantity, setVariationValue }) => {
    const {
        useCart: [cart, setCart],
        useToast: [toast, setToast]
    } = useAppHooks()

    const handleAddToCart = () => {
        if (cart) {
            let updatedCart
            const indexProduct = productInCart(cart, product, variationValue)
            if (indexProduct > -1) {
                updatedCart = updateCartByIndex(cart, indexProduct, quantity, variationValue)
                setCart(updatedCart)
                setToast({
                    title: "Notification",
                    body: `You successfully added ${product.name}-${variationValue} in your cart`,
                    role: "success"
                })
            }
            else {

            }
        }
        else {
            const newCart = createCart(product, quantity, variationValue)
            setCart(newCart)
            setToast({
                title: "Notification",
                body: `You successfully added ${product.name}-${variationValue} in your cart`,
                role: "success"
            })
        }
        setQuantity(0)
        setVariationValue("")
    }

  return (
      <>
        <button
            disabled={!variationValue || quantity === 0}
            className={`btn btn-${(variationValue && quantity > 0) ? "success" : "dark"} w-100 block`}
            onClick={handleAddToCart}
        >
            ADD TO CART
        </button>
      </>
  )
}

export default AddToCartBtn
