import { useEffect, useContext } from "react"
import { AppContext } from "../../context/AppContext"
import { getCart } from "../../utils/cart.util"

const CartNav = () => {
    const [cart, setCart] = useContext(AppContext)

    useEffect(() => {
        if (getCart()) {
            const comparedCart = getCart()
            if (!cart) {
                setCart(comparedCart)
            }
            else if (cart !== getCart()) {
                setCart(comparedCart)
            }
        }
    }, [getCart])

  return (
      <>
        <span>
            Cart
        </span>
        <span className="ml-2 px-2 py-1 bg-dark rounded-circle">
            {cart && cart.totalProductsCount ? cart.totalProductsCount : 0}
        </span>
        <span className="ml-2">
            {cart && cart.totalProductsPrice} â‚¬
        </span>
      </>
  )
}

export default CartNav
