import useCart from "../../hooks/useCart"

const CartNav = () => {
    const cart = useCart()

  return (
      <>
        <span>
            Cart
        </span>
        <span className="ml-2 px-2 py-1 bg-dark rounded-circle">
            {cart && cart.totalProductsCount ? cart.totalProductsCount : 0}
        </span>
        <span className="ml-2">
            {cart && cart.totalProductsPrice + " €"}
        </span>
      </>
  )
}

export default CartNav
