import { useAppHooks } from "../../context/AppContext"
import { createCart, updateCart } from "../../utils/cart.util"

const AddToCartBtn = ({ product, variationValue, quantity, setQuantity, setVariationValue }) => {
    const {useCart: [cart, setCart]} = useAppHooks()

    const handleAddToCart = () => {
        if (cart) {
            const updatedCart = updateCart(cart, product, quantity, variationValue)
            setCart(updatedCart)
            setToast({
                title: "Notification",
                body: `You successfully added ${product.name}-${variationValue} from your cart`,
                role: "success"
            })
        }
        else {
            const newCart = createCart(product, quantity, variationValue)
            setCart(newCart)
            setToast({
                title: "Notification",
                body: `You successfully added ${product.name}-${variationValue} from your cart`,
                role: "success"
            })
        }
        setQuantity(0)
        setVariationValue("")
    }

  return (
      <>
        <button
            disabled={!variationValue || quantity === 0}
            className={`btn btn-${(variationValue && quantity > 0) ? "success" : "dark"} w-100 block`}
            onClick={handleAddToCart}
        >
            ADD TO CART
        </button>
      </>
  )
}

export default AddToCartBtn
