import React from 'react'
import useCart from '../hooks/useCart'

export const AppContext = React.createContext([
    null,
    () => {}
])

export const AppProvider = props => {
    const [cart, setCart] = useCart()

    return (
        <AppContext.Provider value={[cart, setCart]}>
            {props.children}
        </AppContext.Provider>
    )
}
