export const getCart = () => {
    if (localStorage.getItem("woo-next-cart")) {
        return localStorage.getItem("woo-next-cart")
    }
    return null
}

export const createCart = (product, quantity, variationValue) => {
    let rawPrice = parseFloat(product.price.replace(",", ".").substring(1))
    let indexVariation = product.variations.nodes.findIndex(variation => variation.attributes.nodes[0].value === variationValue)
    if (indexVariation > -1) {
        const newProduct = {
            quantity,
            totalPrice: parseFloat((quantity * rawPrice).toFixed(2)),
            variation: variationValue,
            productVariation: product
        }
        const items = [newProduct]
        const newCart = {
            items,
            totalProductsCount: 1,
            totalProductsPrice: newProduct.totalPrice,
        }
        localStorage.setItem("woo-next-cart", JSON.stringify(newCart))
    }
    return null
}

export const updateCart = (cart, product, quantity, variationValue) => {
    const isProductMatchIndex = cart.items.findIndex(item => item.variation === variationValue)
    let rawPrice = parseFloat(product.price.replace(",", ".").substring(1))
    if (isProductMatchIndex > -1) {
        cart.items[isProductMatchIndex].quantity += quantity
        cart.items[isProductMatchIndex].totalPrice = parseFloat((rawPrice * cart.items[isProductMatchIndex].quantity).toFixed(2))
        cart.totalProductsPrice = cart.items.reduce((acc, item) => acc + parseFloat(item.totalPrice), 0)
        localStorage.setItem("woo-next-cart", JSON.stringify(cart))
        return cart
    }
    else {
        let items = [
            ...cart.items,
            {
                quantity,
                totalPrice: (quantity * rawPrice).toFixed(2),
                productVariation: product,
                variation: variationValue
            }
        ]

        const updatedCart = {
            totalProductsPrice: items.reduce((acc, item) => acc + parseFloat(item.totalPrice), 0),
            totalProductsCount: cart.totalProductsCount + 1,
            items
        }
        localStorage.setItem("woo-next-cart", JSON.stringify(updatedCart))
        return updatedCart
    }
}

export const removeItemFromCart = (cart, indexProduct) => {
    const updatedCart = {
        items: cart.items.filter((item, index) => index !== indexProduct),
        totalProductsCount: cart.totalProductsCount - 1,
        totalProductsPrice: cart.totalPrice - cart.items[indexProduct].totalPrice
    }
    localStorage.setItem("woo-next-cart", JSON.stringify(updatedCart))
    return updatedCart
}
