import Link from 'next/link'
import { useState } from 'react'
import { useAppHooks } from '../../context/AppContext'
import { removeItemFromCart } from '../../utils/cart.util'
import { productRawPrice } from '../../utils/product.util'

const CartItem = ({ item, index }) => {
    const {
        useCart: [cart, setCart],
        useToast: [toast, setToast]
    } = useAppHooks()
    const [quantity, setQuantity] = useState(item.quantity || 0)
    const [variationValue, setVariationValue] = useState(item.variation || "")

    const handleClick = index => {
        setToast({
            title: "Notification",
            body: "You successfully removed this product from your cart",
            role: "success"
        })
        const updatedCart = removeItemFromCart(cart, index)
        setCart(updatedCart)
    }

  return (
    <tr>
        <th scope="row">
            <span
                className="badge badge-danger"
                onClick={() => handleClick(index)}
            >
                x
            </span>
        </th>
        <th>
            <Link as={`/product/${item.productVariation.id}`} href="/product/[id]">
                <a>{item.productVariation.name}</a>
            </Link>
        </th>
        <th>
            <select onChange={e => setVariationValue(e.target.value, index)}>
                <option value="" selected={!variationValue} />
                {
                    item.productVariation.variations.nodes.map(variation => (
                        <option
                            key={variation.attributes.nodes[0].value}
                            selected={variationValue === variation.attributes.nodes[0].value}
                            value={variation.attributes.nodes[0].value}>
                            {variation.attributes.nodes[0].value}
                        </option>
                    ))
                }
            </select>
        </th>
        <th>{productRawPrice(item.productVariation.price)} â‚¬</th>
        <th>
            <input
                type="number"
                value={quantity}
                onChange={e => setQuantity(e.target.value >= 1 ? (+e.target.value) : 0)}
            />
        </th>
        <th>{item.totalPrice} â‚¬</th>
    </tr>
  )
}

export default CartItem
