import Link from 'next/link'
import { useEffect, useState } from 'react'
import { useAppHooks } from '../../context/AppContext'
import { updateCartItemByIndex, removeItemFromCart } from '../../utils/cart.util'
import { productRawPrice } from '../../utils/product.util'

const CartItem = ({ item, index }) => {
    const {
        useCart: [cart, setCart],
        useToast: [toast, setToast]
    } = useAppHooks()
    const [quantity, setQuantity] = useState(item.quantity || 0)

    const handleClick = index => {
        setToast({
            title: "Notification",
            body: "You successfully removed this product from your cart",
            role: "success"
        })
        const updatedCart = removeItemFromCart(cart, index)
        setCart(updatedCart)
    }

    const handleChange = (e, index) => {
        if (index > -1) {
            const updatedCart = updateCartItemByIndex(cart, index, e.target.quantity)
            console.log(updatedCart)
            setCart(updatedCart)
        }
        setQuantity(e.target.value)
    }

    // useEffect(() => {
    //     if (index > -1) {
    //         const updatedCart = updateCartItemByIndex(cart, index, quantity)
    //         console.log(updatedCart)
    //         setCart(updatedCart)
    //     }
    // }, [quantity])

  return (
    <tr>
        <th scope="row">
            <span
                className="badge badge-danger"
                onClick={() => handleClick(index)}
            >
                x
            </span>
        </th>
        <th>
            <Link as={`/product/${item.productVariation.id}`} href="/product/[id]">
                <a className="text-white">{item.productVariation.name}</a>
            </Link>
        </th>
        <th>
            <strong>{item.variation}</strong>
        </th>
        <th>{productRawPrice(item.productVariation.price)} €</th>
        <th>
            <input
                type="number"
                value={quantity}
                  onChange={e => handleChange(+e.target.value, index)}
            />
        </th>
        <th>{item.totalPrice} €</th>
    </tr>
  )
}

export default CartItem
