import { useContext } from "react"
import { AppContext } from "../../context/AppContext"
import { createCart, updateCart } from "../../utils/cart.util"

const AddToCartBtn = ({ product, variationValue, quantity }) => {
    const [cart, setCart] = useContext(AppContext)

    const handleAddToCart = () => {
        if (cart) {
            const updatedCart = updateCart(cart, product, quantity, variationValue)
            console.log(updatedCart)
            setCart(updatedCart)
        }
        else {
            const newCart = createCart(product, quantity, variationValue)
            setCart(newCart)
        }
    }

  return (
      <>
        <button
            disabled={!variationValue || quantity === 0}
            className={`btn btn-${(variationValue && quantity > 0) ? "success" : "dark"} w-100 block`}
            onClick={handleAddToCart}
        >
            ADD TO CART
        </button>
      </>
  )
}

export default AddToCartBtn
